mapper.sql:
 			WHERE


		 		 	inquiryTitle 


		 		 	inquiryDetail 


	 			LIKE '%' || #{search} || '%'

	 				AND inquiryCategory = #{category}


	 				AND memberId = #{memberId}

mapper.select:
 		SELECT * FROM(
		SELECT ROWNUM R,A.*
			FROM(
				SELECT * FROM Inquiry  
			) A
		)
		WHERE R BETWEEN #{startRow} AND #{lastRow}
